dt-api.example:
var table = $('#myTable').DataTable();
var button = table.button( 0 );
var counter = 0;

button
	.text( 'Click counter: 0' )
	.action( function () {
		counter++;
		this.text( 'Click counter: '+counter );
	} );

var table = $('#myTable').DataTable();

table.button( 0 ).text( function ( dt, button, config ) {
	return dt.i18n( 'buttons.input', 'Input' );
} );
