dt-api.example:
var table = $('#myTable').DataTable();

var data = table.buttons.exportData();
// Do something with the 'data' variable

var table = $('#myTable').DataTable();

var data = table.buttons.exportData( {
	modifier: {
		selected: true
	}
} );
// Do something with the 'data' variable

var table = $('#myTable').DataTable();

var data = table.buttons.exportData( {
	columns: ':visible'
} );
// Do something with the 'data' variable

var table = $('#myTable').DataTable();

var data = table.buttons.exportData( {
	format: {
		header: function ( data, columnIdx ) {
			return columnIdx +': '+ data;
		}
	}
} );
// Do something with the 'data' variable
