dt-button.example:
$('#myTable').DataTable( {
	buttons: [
		{
			extend: 'copyFlash',
			text: 'Copy current page',
			exportOptions: {
				modifier: {
					page: 'current'
				}
			}
		}
	]
} );

$('#myTable').DataTable( {
	language: 
		buttons: {
			copyTitle: 'Data copied',
			copyInfo: {
				_: 'Copied %d items',
				1: 'Copied a single item'
			}
		}
	},
	buttons: [
		'copyFlash'
	]
} );
