dt-button.example:
$('#myTable').DataTable( {
	dom: 'Bfrtip',
	lengthMenu: [
		[ 10, 25, 50, -1 ],
		[ '10 rows', '25 rows', '50 rows', 'Show all' ]
	],
	buttons: [
		'pageLength'
	]
} );

$('#myTable').DataTable( {
	dom: 'Bfrtip',
	language: {
		buttons: {
			pageLength: {
				_: "Afficher %d éléments",
				-1: "Tout afficher"
			}
		}
	},
	buttons: [
		'pageLength'
	]
} );
