dt-button.example:
$('#myTable').DataTable( {
	buttons: [
		{
			extend: 'print',
			text: 'Print current page',
			autoPrint: false
		}
	]
} );

$('#myTable').DataTable( {
	buttons: [
		{
			extend: 'print',
			text: 'Print current page',
			exportOptions: {
				modifier: {
					page: 'current'
				}
			}
		}
	]
} );
