dt-option.example:
$('#myTable').DataTable( {
	buttons: {
		buttons: [
			{
				text: 'Alert',
				action: function ( e, dt, node, config ) {
					alert( 'Activated!' );
					this.disable(); // disable button
				}
			}
		]
	}
} );

new $.fn.dataTable.Buttons( table, {
	buttons: [
		{ 
			text: 'Copy to div',
			action: function ( e, dt, node, config ) {
				// Copy an array based DataTables' data to another element
				$('#output').html( dt.data().map( function (row) {
					return row.join(' | ' );
				} ).join('<br>');
			}
		}
	]
} );
