dt-option.example:
$('#myTable').DataTable( {
	buttons: {
		buttons: [
			{ extend: 'copy', className: 'copyButton' },
			{ extend: 'excel', className: 'excelButton' }
		]
	}
} );

new $.fn.dataTable.Buttons( table, {
	buttons: [
		{ extend: 'copy', className: 'copyButton' },
		{ extend: 'excel', className: 'excelButton' }
	]
} );
