dt-option.example:
$('#myTable').DataTable( {
	buttons: {
		buttons: [
			{
				text: 'Enabled only with selected item',
				init: function ( dt, node, config ) {
					var that = this;

					dt.on( 'select.dt.DT deselect.dt.DT', function () {
						that.enable( dt.rows( { selected: true } ).any() );
					} );

					this.disable();
				}
			}
		]
	}
} );
