dt-example.js:
$(document).ready(function() {
	var counter = 1;

	$('#example').DataTable( {
		dom: 'Bfrtip',
		buttons: [
			{
				text: 'Add new button',
				action: function ( e, dt, node, config ) {
					dt.button().add( 1, {
						text: 'Button '+(counter++),
						action: function () {
							this.remove();
						}
					} );
				}
			}
		]
	} );
} );
