dt-example.js:
$(document).ready(function() {
	$('#example').DataTable( {
		dom: 'Bfrtip',
		buttons: [
			{
				extend: 'colvisGroup',
				text: 'Office info',
				show: [ 1, 2 ],
				hide: [ 3, 4, 5 ]
			},
			{
				extend: 'colvisGroup',
				text: 'HR info',
				show: [ 3, 4, 5 ],
				hide: [ 1, 2 ]
			},
			{
				extend: 'colvisGroup',
				text: 'Show all',
				show: ':hidden'
			}
		]
	} );
} );
