dt-example.js:
$(document).ready(function() {
	$('#example').DataTable( {
		dom: 'Bfrtip',
		buttons: [ {
			extend: 'excelHtml5',
			customize: function ( xlsx ){
				var sheet = xlsx.xl.worksheets['sheet1.xml'];

				// jQuery selector to add a border
				$('row c[r*="10"]', sheet).attr( 's', '25' );
			}
		} ]
	} );
} );
