dt-example.js:
$(document).ready(function() {
	$('#example').DataTable( {
        ajax: '../../../../examples/ajax/data/objects.txt',
        columns: [
            { data: 'name' },
            { data: 'position' },
            { data: 'office' },
            { data: 'extn' },
            { data: 'start_date' },
            { data: 'salary', render: function (data, type, row) {
            	return type === 'export' ?
            		data.replace( /[$,]/g, '' ) :
            		data;
            } }
        ],
		dom: 'Bfrtip',
		buttons: [
			{
				extend: 'copyHtml5', 
				exportOptions: { orthogonal: 'export' }
			},
			{
				extend: 'excelHtml5', 
				exportOptions: { orthogonal: 'export' }
			},
			{
				extend: 'pdfHtml5', 
				exportOptions: { orthogonal: 'export' }
			}
		]
	} );
} );
