dt-example.js:
$(document).ready(function() {
	$('#example').DataTable( {
		dom: 'Bfrtip',
		buttons: [
			{
				text: 'Button <u>1</u>',
				key: '1',
				action: function ( e, dt, node, config ) {
					alert( 'Button 1 activated' );
				}
			},
			{
				text: 'Button <u><i>alt</i> 2</u>',
				key: {
					altKey: true,
					key: '2'
				},
				action: function ( e, dt, node, config ) {
					alert( 'Button 2 activated' );
				}
			}
		]
	} );
} );
